import React from "react";
import useStyles from "./styles";

const Home = (props) => {
	const classes = useStyles();
	return (
		<div className={classes.container}>
			<h4>{props.head}</h4>
			<p>{props.para}</p>
			<a href="/login">
				<button type="button">Login</button>
			</a>
			<p>
				<br />
				Don't have an account?
			</p>
			<button>Register</button>
		</div>
	);
};

export default Home;

