import { makeStyles } from "@material-ui/core/styles";

export default makeStyles((theme) => ({
	mainContainer: {
		width: "50%",
		margin: "auto",
	},
	textCenter: {
		textAlign: "center",
	},
	topPadding: {
		paddingTop: "1rem",
	},
}));
